
// Mínimo simulador local si jsPDF no está cargando desde CDN
var jsPDF = function() {
  return {
    setFont: function(){},
    setFontSize: function(){},
    text: function(text, x, y) { console.log(text); },
    addPage: function() {},
    save: function(filename) {
      const blob = new Blob(["PDF generado (simulado)"], {type: "application/pdf"});
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      a.click();
      URL.revokeObjectURL(url);
    },
    addImage: function() {}
  };
};
